import React from 'react';
import {connect} from 'react-redux';
import {createUserAction} from '../src/actions/user';

function index(props) {
  let name = 'Muhammad Shamim';
  let mobile = '01400333590';
  let address = '20/8, Tolarbag, Mirpur-1, Dhaka, Bangladesh';

  let userData = {
    name: name,
    mobile: mobile,
    address: address,
  };


  return (
    <>
      <h1>Bismillahir Rahmanir Rahim</h1>
      <h2>Saved Data: {props.user}</h2>
      <h2>
        <button
          onClic
        >
          Click
        </button>
      </h2>
    </>
  );
}

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = (dispatch) => ({
  createUser: (userData) => {
    console.log(userData);
    dispatch(createUserAction(userData));
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(index);
