// import React from 'react';
import {connect} from 'react-redux';
import {createUserAction} from 'redux/actions/userActions';
let userData = {
  name: 'name',
  mobile: 'mobile',
  address: 'address',
};

import React, {Component} from 'react';

class index extends Component {
  handleCreateUser = () => {
    console.log('object');
    this.props.createUser('userData');
  };

  render() {
    const user_list = this.props.userData.users.map((user) => {
      return (
        <>
          <p className="username">Name: {user.name}</p>
          <p className="mobile">Name: {user.mobile}</p>
          <p className="username">Name: {user.address}</p>
        </>
      );
    });
    return (
      <div className="">
        <h1>Register a user</h1>
        <h2>
          <button onClick={this.handleCreateUser}>Click</button>
        </h2>
        <div className="all-user-container">{this.props.userData.users}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = (dispatch) => ({
  createUser: (userData) => {
    console.log(userData);
    dispatch(createUserAction(userData));
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(index);
